import React, {useState, useEffect, useContext, useRef} from 'react'
import {SortableContainer, SortableElement} from 'react-sortable-hoc';
import arrayMove from 'array-move';

function getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
        const cookies = document.cookie.split(';');
        for (let i = 0; i < cookies.length; i++) {
            const cookie = cookies[i].trim();
            // Does this cookie string begin with the name we want?
            if (cookie.substring(0, name.length + 1) === (name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
}


function fancyTimeFormat(duration)
{   
    // Hours, minutes and seconds
    var hrs = ~~(duration / 3600);
    var mins = ~~((duration % 3600) / 60);
    var secs = ~~duration % 60;

    // Output like "1:01" or "4:03:59" or "123:03:59"
    var ret = "";

    if (hrs > 0) {
        ret += "" + hrs + ":" + (mins < 10 ? "0" : "");
    }

    ret += "" + mins + ":" + (secs < 10 ? "0" : "");
    ret += "" + secs;
    return ret;
}

const Timer = ({ seconds, onTimeUpdate }) => {
    // initialize timeLeft with the seconds prop
    const [timeLeft, setTimeLeft] = useState(seconds);

    useEffect(() => {
      // exit early when we reach 0
        if (!timeLeft) return;

      // save intervalId to clear the interval when the
      // component re-renders
        const intervalId = setInterval(() => {
        if (timeLeft > 0) {
            setTimeLeft(timeLeft - 1);
            onTimeUpdate(timeLeft)
        }
        }, 1000);

      // clear interval on re-render to avoid memory leaks
        return () => clearInterval(intervalId);
      // add timeLeft as a dependency to re-rerun the effect
      // when we update it
    }, [timeLeft]);

    useEffect(() => {
        setTimeLeft(seconds)
    }, [seconds])

    return (
    <div>
        <h1 className='has-text-success'>{fancyTimeFormat(timeLeft)}</h1>
    </div>
    );
    };



const StartTimer = ({ value, onTimerStart, onTimerPause }) => {

    const [timer, setTimer] = useState({'state': 'paused', 'seconds': '0'});
    const {active, setActive} = useContext(ActiveTask);

    function startTimer() {
        setTimer({'state': 'running', 'seconds': value.remaining});
        onTimerStart();
        setActive(true);
    }

    function pauseTimer() {
        setTimer({'state': 'paused', 'seconds': '0'});
        setActive(false);
        onTimerPause();
    }

    function timeUpdate(s) {
        var url = 'http://127.0.0.1:8000/api/task-update/' + value.id + '/';
        var csrf_token = getCookie('csrftoken');

        var new_remaining = value
        new_remaining.remaining = s

        fetch(url, {
            'method': 'POST',
            'headers': {
                'Content-Type': 'application/json',
                'X-CSRFToken': csrf_token,
            },
            'body': JSON.stringify(value)
            });

        }


    return (
        <div className={"level-item " + (value.completed ? 'is-hidden' : '')}>

            {timer.state === 'paused' && active === false &&
            <a href="/#" onClick={startTimer}>
                <span className="icon has-text-success">
                    <i className="fas fa-play"></i>
                </span>
            </a>
            }

            {timer.state === 'running' &&
            <a href="/#" onClick={pauseTimer}>
                <span className="icon has-text-success">
                    <i className="fas fa-pause"></i>
                </span>
            </a>
            }

            {timer.state === 'running' && 
            <Timer seconds={timer.seconds} onTimeUpdate={timeUpdate}/>
            }

        </div>
    )
}



const SortableItem = SortableElement(({value, onActivateTask, onDeleteTask, onFinishedTask}) => {
    const [active_style, setActiveStyle] = useState(false)

    function timerStarted() {
        onActivateTask(value.id);
        setActiveStyle(true);
    }

    return (
        <li className={"box " + (active_style ? 'task-is-active': '')}>
            <div className="level is-mobile">
                <div className="level-left">
                    <input className={" " + (value.completed ? 'is-hidden' : '')} type="checkbox" onChange={() => onFinishedTask(value)}/>
                </div>

                <div className="level-item">
                    <h1 className={"title is-5 " + (value.completed ? 'is-completed': '')}>
                        {value.title}
                    </h1>
                </div>

                <div className="level-right">

                    <StartTimer value={value} onTimerStart={timerStarted} onTimerPause={() => setActiveStyle(false)}/>

                    <div className={"level-item " + (value.completed ? 'is-hidden': '')}>
                        <a href="#/">
                            <span className="icon has-text-warning">
                                <i className="fas fa-edit"></i>
                            </span>
                        </a>
                    </div>

                    <div className="level-item">
                        <a href="#/" onClick={e => onDeleteTask(value)}>
                            <span className="icon has-text-danger">
                                <i className="fas fa-lg fa-times"></i>
                            </span>
                        </a>
                    </div>

                </div>

            </div>
        </li>
    )
});

const SortableComponent = SortableContainer(({items, onFocusTask, onDeleteTask, onFinishedTask}) => {

    function setActiveTask(taskId) {
        var index = items.findIndex(i => i.id === taskId);
        onFocusTask(index);
    }

    return (
    <ul>
        {items.map((value, index) => (
            <SortableItem key={value.id} index={index} value={value} onActivateTask={setActiveTask} onDeleteTask={onDeleteTask} onFinishedTask={onFinishedTask}/>
        ))}
    </ul>
    )
})


// Context
export const ActiveTask = React.createContext(false)

export default function SortableList({ newTaskCreated, completedTasksList }) {
    const [tasks, setTasks] = useState([]);
    const [active, setActive] = useState(false);
    const active_task = {active, setActive};
    const isInitialMount = useRef(true);
    const [completedList, setList] = useState(false);

    var fetchTasks = async () => {
        const url = 'http://127.0.0.1:8000/api/task-list/' + (completedList ? 'completed/' : 'active/')
        console.log(completedList)
        try {
            const response = await fetch(url);
            return response.ok ? response.json() : null;
        } catch (error) {
            console.log(error);
            return null;
        }
    }

    const taskUpdate = async (obj, csrf_token) => {
        const url = 'http://127.0.0.1:8000/api/task-update/' + obj.id + '/'

        try {
            const update = await fetch(url, {
                'method': 'POST',
                'headers': {
                    'Content-Type': 'application/json',
                    'X-CSRFToken': csrf_token,
                },
                'body': JSON.stringify(obj)
            })
        } catch (error) {
            console.log(error)
        }
    }

    function updateIndex(data) {
        var csrf_token = getCookie('csrftoken');

        data.forEach((task, index) => {
            task.order = index;
            taskUpdate(task, csrf_token);
        });
    };

    useEffect(() => {
        if (completedTasksList === 'completedList') {
            setList(true);
        }
    }, [completedTasksList])

    //Update list when new task is created
    useEffect(() => {
        if (isInitialMount.current) {
            isInitialMount.current = false;
        } else {
            console.log('new task created')
            fetchTasks().then(data => {
                data.sort((a, b) => (a.order > b.order) ? 1 : ((b.order > a.order) ? -1 : 0));
                var index = data.findIndex(i => i.title === newTaskCreated.title);
                data = arrayMove(data, index, 0);
                data.forEach((value, i) => {
                    value.order = i;
                })
                setTasks(data);
                updateIndex(data);
            })
        }

    }, [newTaskCreated])

    useEffect(() => {
        fetchTasks()
        .then(data => {
            data.sort((a, b) => (a.order > b.order) ? 1 : ((b.order > a.order) ? -1 : 0));
            setTasks(data);
            }
        )
        // return () => {
        //     cleanup
        // };
    }, []);

    const onSortEnd =  ({oldIndex, newIndex}) => {
        var newOrder = [...tasks];
        newOrder = arrayMove(newOrder, oldIndex, newIndex);

        updateIndex(newOrder);
        setTasks(newOrder);

    };

    function deleteTask(e) {
        var newTasks = tasks.filter(i => i.id !== e.id);
        setTasks(newTasks);

        var csrf_token = getCookie('csrftoken');
        var url = 'http://127.0.0.1:8000/api/task-delete/' + e.id + '/';

        fetch(url, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json',
                'X-CSRFToken': csrf_token,
            }
        })
    }

    function getCurrentDate() {
        var date = new Date();
        var dd = String(date.getDate()).padStart(2,'0');
        var mm = String(date.getMonth() + 1).padStart(2,'0');
        var year = date.getFullYear();

        var today = year + '-' + mm + '-' + dd;
        return today;
    }

    function markTaskAsComplete(e) {
        var index = tasks.findIndex(i => i.id === e.id)
        var updates = [...tasks];
        updates[index].completed = true;
        updates[index].finish_date = getCurrentDate();
        var csrf_token = getCookie('csrftoken')

        setTasks(updates);
        taskUpdate(updates[index], csrf_token);
    }


    function focusTask(taskIndex) {
        var newOrder = arrayMove(tasks, taskIndex, 0);
        setTasks(newOrder)
    };

    return (
        <>
        <ActiveTask.Provider value={active_task}>
            <SortableComponent items={tasks} onSortEnd={onSortEnd} onFocusTask={focusTask} onDeleteTask={deleteTask} onFinishedTask={markTaskAsComplete}/>
        </ActiveTask.Provider>
        </>
    )
}
