import arrayMove from 'array-move';
import React, {useState, useEffect} from 'react'
import { LineChart, Line, CartesianGrid, XAxis, YAxis, Tooltip } from 'recharts';

Date.prototype.addDays = function(days) {
    var date = new Date(this.valueOf());
    date.setDate(date.getDate() + days);
    return date;
}

export default function Chart() {
    const [data, setData] = useState(null);

    function dateToStr(date) {
        var dd = String(date.getDate()).padStart(2,'0');
        var mm = String(date.getMonth() + 1).padStart(2,'0');
        var year = date.getFullYear();

        var today = year + '-' + mm + '-' + dd;
        return today;
    }

    function dataHeaders() {
        var today = new Date();
        var arr = [today];
        var i;
        for (i = -1; i > -8; i--) {
            var current = today;
            arr.unshift(current.addDays(i));
        };
        var headers = arr.map(d => dateToStr(d))
        return headers;
    }

    useEffect(() => {
        const runCall = async () => {
            let values = await fetchData();
            // const headers = values.map(h => h.finish_date)
            // headers = headers.filter(Boolean)
            var result = new Map();

            values.forEach(e => {
                if(result.get(e.finish_date)) {
                    result.set(e.finish_date, result.get(e.finish_date) + 1);
                } else {
                    result.set(e.finish_date, 1);
                }
            });

            var headers = dataHeaders();
            var data = []

            headers.forEach(d => {
                var obj= {}
                obj['date'] = d;
                obj['completed'] = (result.get(d) ? result.get(d): 0);
                data.push(obj)
            })

            return data;
        }

        const fetchData = async () => {
            const url = 'http://127.0.0.1:8000/api/task-list/'

            try {
                const response = await fetch(url);
                return response.ok ? response.json() : null;
            } catch (error) {
                console.log(error);
                return null;
            }
        }

        setData(runCall())

    }, [])

    return (
        // <h1>Graph</h1>
        <BarChart
            width={500}
            height={300}
            data={data}
            margin={{
            top: 5, right: 30, left: 20, bottom: 5,
            }}
            >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="pv" fill="#8884d8" />
            <Bar dataKey="uv" fill="#82ca9d" />
      </BarChart>
    )
}
