import React, {useState} from 'react'

export function SortableItem(props) {
    return (
        <li className="box">
            <h1 className="title">
                {props.title}
            </h1>
        </li>
    )
}

export default function SortableList() {
    const [tasks, setTasks] = useState([
        {id: 1, title: 'task 1'},
        {id: 2, title: 'task 2'},
    ])

    return (
        <ul>
            {tasks.map(task => <SortableItem {...task} />)}
        </ul>
    )
}
